-- TALLER - SQL
-- PASOS INCIALES PARA LA CREACION DE UNA BASE DE DATOS
-- Inicialmente se crea la base de datos
CREATE DATABASE comercial;

-- Aqui le decimos nos dirigimos asi la base de datos creada
USE comercial;

-- Creamos la primera tabla
CREATE TABLE oficina (
  id_oficina INT UNIQUE AUTO_INCREMENT PRIMARY KEY NOT NULL,
  ciudad VARCHAR(100) NOT NULL,
  telefono VARCHAR(20) NOT NULL,
  direccion VARCHAR(200) NOT NULL,
  departamento VARCHAR(100) NOT NULL,
  pais VARCHAR(100) NOT NULL,
  creado TIMESTAMP
);

-- Llenamos con registros la tabla oficina
-- No es necesario agregar el id_oficina ya que se le dio la propiedad de AUTO_INCREMENT y unico

-- Creamos la siguiente tabla que hace referencia a los empleados del comercial
CREATE TABLE empleado (
  documento INT UNIQUE PRIMARY KEY NOT NULL,
  nombre VARCHAR(100) NOT NULL,
  apellido VARCHAR(100) NOT NULL,
  telefono VARCHAR(20) NOT NULL,
  correo VARCHAR(100) UNIQUE NOT NULL,
  id_oficina INT NOT NULL,
  jefe VARCHAR(100) NOT NULL,
  cargo TEXT NOT NULL,
  creado TIMESTAMP
);

-- Llenamos los campos con registros correspondientes
-- NOTA: tener en cuenta que el campo id_oficina tiene que hacer referencia a la identificacion de la tabla oficina

-- Creamos la ultima tabla (cliente)
CREATE TABLE cliente (
  id_cliente INT UNIQUE AUTO_INCREMENT PRIMARY KEY,
  empresa VARCHAR(100),
  nombre VARCHAR(100),
  apellido VARCHAR(100),
  telefono NUMERIC,
  direccion VARCHAR(100),
  departamento VARCHAR(100),
  pais VARCHAR(100),
  empleado_atiende NUMERIC NOT NULL,
  creado TIMESTAMP
);

-- Llenamos la tabla con los valores correspondientes
-- NOTA: Tener en cuenta que empleado_atiende hace referencia al documento de la tabla anterior
